var PROC = require('app/procedure');
var DB = require('app/database');
var AU = require('app/controller/auth-util');

function list_games(username, token, msg) {
    AU.auth(username, token, msg, function () {
        PROC.find_games(function (err, docs) {
            if (docs) {
                msg.send(docs);
            } else {
                msg.error(1);
            }
        });
    });
}

exports.list_games = list_games;