var RH = require('app/request-handler');

function api_post_handler(req, res) {
    var id = req.params.id,
        data = req.body,
        ip = req.ip;
    if (ip === '') {
        id = '';
    }
    switch (id) {
    case 'test':
        res.send('test');
        break;
	case 'auth':
		RH.auth(req, res);
		break;
    default:
        res.status(400).send('Bad Request');
    }
}

exports.api_string = '/api/:id';
exports.api_post_handler = api_post_handler;